 int start = 4; 
int end = 5; 

if (start < end) 
{ 
   if (end > 0) 
   { 
      start += 2; 
      end++; 
   } 
   else 
   { 
      end += 3; 
   } 
} 

if (start < end) 
{ 
   if (end == 0) 
   { 
      end += 2; 
      start++; 
   } 
   else 
